CREATE TABLE "QUESTION_FAF" ( `id` INTEGER, `categorie` INTEGER NOT NULL, `theme` TEXT NOT NULL, `question` TEXT NOT NULL, `reponse` TEXT NOT NULL, `reference` INTEGER NOT NULL UNIQUE, `club` TEXT NOT NULL, `dateReception` TEXT NOT NULL, `version` INTEGER NOT NULL, `difficulte` INTEGER NOT NULL, `active` INTEGER, PRIMARY KEY(`id`) );

CREATE TABLE "QUESTION_NPG" ( `id` INTEGER, `question` TEXT NOT NULL, `reponse` TEXT NOT NULL, `difficulte` INTEGER NOT NULL, `reference` INTEGER NOT NULL UNIQUE, `club` TEXT NOT NULL, `dateReception` TEXT NOT NULL, `version` INTEGER NOT NULL, `active` INTEGER NOT NULL, PRIMARY KEY(`id`) );
CREATE TABLE "QUESTION_NPG_ANOMALIE" ( `id` INTEGER, `reference` INTEGER NOT NULL, `version` INTEGER NOT NULL, `date_anomalie` TEXT NOT NULL, `type_anomalie` INTEGER NOT NULL, `cause` TEXT NOT NULL, `lecteur` TEXT NOT NULL, PRIMARY KEY(`id`) );
CREATE TABLE "QUESTION_NPG_LECTURE" ( `id` INTEGER NOT NULL, `question_id` INTEGER NOT NULL, `date_lecture` DATETIME NOT NULL, `lecteur` TEXT, `reference` TEXT NOT NULL, PRIMARY KEY(`id`) );
